def _plot_momentum(cci, dmi, macd, rsi, stoch):
    fig, axs = plt.subplots(5)
    fig.suptitle('Momentum Indicators')

    axs[0].plot(cci)
    axs[0].set_title('CCI')

    axs[1].plot(dmi[0])
    axs[1].plot(dmi[1])
    axs[1].plot(dmi[2])
    axs[1].set_title('DMI')

    axs[2].plot(macd[0])
    axs[2].plot(macd[1])
    axs[2].plot(macd[2])
    axs[2].set_title('MACD')

    axs[3].plot(rsi)
    axs[3].set_title('RSI')

    axs[4].plot(stoch[0])
    axs[4].plot(stoch[1])
    axs[4].set_title('STOCHASTIC')

    plt.show()

def _plot_overlap(bbands, ema, psar, rma, sma, vwap, wma):
    fig, axs = plt.subplots(7)
    fig.suptitle('Overlap Studies')

    axs[0].plot(bbands[0])
    axs[0].plot(bbands[1])
    axs[0].plot(bbands[2])
    axs[0].set_title('BBANDS')

    axs[1].plot(ema)
    axs[1].set_title('EMA')

    axs[2].plot(psar)
    axs[2].set_title('PSAR')

    axs[3].plot(rma)
    axs[3].set_title('RMA')

    axs[4].plot(sma)
    axs[4].set_title('SMA')

    axs[4].plot(vwap)
    axs[4].set_title('VWAP')

    axs[4].plot(wma)
    axs[4].set_title('WMA')

    plt.show()


